import React, { useState } from "react";
import "./DeliveryLinks.css"
import { useRestaurantData } from '../../data/restaurantData';
import Modal from 'react-modal';


function DeliveryLinks({show}){
    //const [links, setLinks] = useState(show)
    const { restaurantData, loading, error } = useRestaurantData();
    const hours = restaurantData.hours
    const [isModalOpen, setIsModalOpen] = useState(false);

    // Fonction pour vérifier si le restaurant est fermé
    const isRestaurantClosed = () => {
        const today = new Date().getDay();
        const hourNow = new Date().getHours() + ':' + new Date().getMinutes();
        const todayHours = hours.find(hour => hour.day_of_week === today);
        
        if (todayHours.is_closed_all_day) return true;
        if (todayHours.is_lunch_closed && hourNow >= todayHours.lunch_open_time && hourNow <= todayHours.lunch_close_time) return true;
        if (todayHours.is_dinner_closed && hourNow >= todayHours.dinner_open_time && hourNow <= todayHours.dinner_close_time) return true;
        
        return false;
    };

    const handleLinkClick = (e) => {
        if (isRestaurantClosed()) {
            e.preventDefault(); // Empêche la redirection
            setIsModalOpen(true); // Ouvre le modal
        }
    };

    // Afficher un message de chargement pendant la récupération des données
    if (loading) return <div className="containerGlobalInfoRestaurant">Chargement des informations...</div>;
    
    // Gérer les erreurs
    if (error) return <div className="containerGlobalInfoRestaurant">Erreur: Impossible de charger les informations</div>;
    
    return(
        <div className="containerDeliveryLinks">

            
                {restaurantData.pickup_option && (
                    <a href="https://platforms.yumco.fr/" onClick={handleLinkClick}>
                        <div className="containerClickAndCollect">
                            <span className="material-symbols-outlined">shopping_bag</span>
                            <p className="text-delivery">CLICK & COLLECT</p>
                        </div>
                    </a>
                )}
            
            {(restaurantData.midday_delivery && new Date().getHours() < 16) || (restaurantData.evening_delivery && new Date().getHours() >= 16) && (
                <a target="blank" href="https://platforms.yumco.fr/" onClick={handleLinkClick}>
                    <div className="containerLinks">
                        <span className="material-symbols-outlined">directions_bike</span>
                        <p className="text-delivery">LIVRAISON</p>
                    </div>
                </a>
            )}

            {restaurantData.reservation_option && (
                <a href="https://platforms.yumco.fr/#reservation" onClick={handleLinkClick}>
                    <div className="containerLinks">
                        <span className="material-symbols-outlined">event_seat</span>
                        <p className="text-delivery">RÉSERVATION</p>
                    </div>
                </a>
            )}
        
            {/* Modal pour afficher le message de fermeture */}
            <Modal isOpen={isModalOpen} onRequestClose={() => setIsModalOpen(false)}>
                <h2>Restaurant Fermé</h2>
                <p>Le restaurant est actuellement fermé. Veuillez revenir plus tard.</p>
                <button onClick={() => setIsModalOpen(false)}>Fermer</button>
            </Modal>
        </div>
    )
}

export default DeliveryLinks
